function displayAtelier1() {
    document.getElementById('jeu-n1').style.top = "0";
    document.getElementById('jeu-n1').style.opacity = "1";
    document.getElementById('jeu-n1').style.zIndex = "200";
}
function gamme1Retour() {
    document.getElementById('jeu-n1').style.top = "-110vh";
    document.getElementById('jeu-n1').style.opacity = "0";
    document.getElementById('jeu-n1').style.zIndex = "0";
}

